function zFill(str,num){
    for(var i=1;i<=num;i++){
        str="0"+str;
    }
    return str;
}

function padLeft(num, size) {
    num = num.toString();
    while (num.length < size) num = "0" + num;
    num = num.substring(num.length, num.length - size);
    return num;
}

function hex2bin(hex){
    return (parseInt(hex, 16).toString(2));
}

function hex2ascii(string){
	var hex  = string.toString();
	var str = '';
	for (var n = 0; n < hex.length; n += 2) {
		str += String.fromCharCode(parseInt(hex.substr(n, 2), 16));
	}
	return str;
}

function decode(key,raw) {
    switch (key) {
        case "dt":
            var nd = new Date(0);
            var epoch = parseInt(raw);
            // msg.epoch=epoch;
            nd.setUTCSeconds(epoch);
            var y = String(nd.getFullYear());
            var m = padLeft(nd.getMonth()+1,2);
            var d = padLeft(nd.getDate(),2);
            var H = padLeft(nd.getHours(),2);
            var i = padLeft(nd.getMinutes(),2);
            var s = padLeft(nd.getSeconds(),2);
            return y + "-" + m + "-" + d + " " + H + ":" + i + ":" + s;
        case "lat":
            var latitude = String(parseInt(raw, 16));
            var filled = zFill(latitude,10-latitude.length);
            var negatif = filled.substring(0,1);
            var decNum = filled.substring(1, 4);
            var afterDec = filled.substring(4, 10);
            var neg = "";
            if (negatif == "1") {
                neg = "-";
            }
            return String(parseFloat(neg + decNum + "." + afterDec));
        case "lng":
            var longitude = String(parseInt(raw, 16));
            filled = zFill(longitude,10-longitude.length);
            negatif = filled.substring(0, 1);
            decNum = filled.substring(1, 4);
            afterDec = filled.substring(4, 10);
            neg = "";
            if (negatif == "1") {
                neg = "-";
            }
            return String(parseFloat(neg + decNum + "." + afterDec));
        case "cogsog":
            return String(raw);
        case "mmsi":
            return String(parseInt(raw, 16));

    }
}


