function runParser(dataMap,parsed,rawMsg,typeName,num){
    var index = 0;
    Object.keys(dataMap).forEach(key => {
        var rawData=rawMsg.substring(index,(dataMap[key]+index));
        // parsed.raw[key]=rawData;
        // var textData=decode(key,rawData);
        // parsed[typeName][key]=textData;
        // index=index+dataMap[key];
        if(num==='obj'){
            parsed.raw[key]=rawData;
            var textData=decode(key,rawData);
            parsed[typeName][key]=textData;
            index=index+dataMap[key];
        }
        else{
            // parsed[typeName]={};
            // parsed[typeName][num]={};
            parsed.raw[key]=rawData;
            var aisData=decode(key,rawData);
            parsed[typeName][String(num)][key]=aisData;
            index=index+dataMap[key];
        }
    });
    return parsed;
}

