function decode(key,raw) {
    switch (key) {
        case "dt":
            var nd = new Date(0);
            var epoch = parseInt(raw, 16);
            nd.setUTCSeconds(epoch);
            var y = String(nd.getFullYear());
            var m = nd.getMonth()+1;
            var d = String(nd.getDate());
            var H = nd.getHours();
            var i = nd.getMinutes();
            var s = nd.getSeconds();
            if(m<10){
                m="0"+String(m);
            }else{
                m=String(m);
            }
            return y + "-" + m + "-" + d + " " + H + ":" + i + ":" + s;
        case "lat":
            var latitude = String(parseInt(raw, 16));
            var filled = zFill(latitude,10-latitude.length);
            var negatif = filled.substring(0,1);
            var decNum = filled.substring(1, 4);
            var afterDec = filled.substring(4, 10);
            var neg = "";
            if (negatif == "0") {
                neg = "-";
            }
            return String(parseFloat(neg + decNum + "." + afterDec));
        case "lng":
            var longitude = String(parseInt(raw, 16));
            filled = zFill(longitude,10-longitude.length);
            negatif = filled.substring(0, 1);
            decNum = filled.substring(1, 4);
            afterDec = filled.substring(4, 10);
            neg = "";
            if (negatif == "0") {
                neg = "-";
            }
            return String(parseFloat(neg + decNum + "." + afterDec));
        case "cogsog":
            var cs = String(parseInt(raw, 16));
            filled = zFill(cs,5-cs.length);
            return filled;
        case "batt":
            return raw.substring(0,1)+"."+raw.substring(1);
        case "stat":
            return padLeft(String(hex2bin(raw)),8);
        case "gpsq_mtr":
            return raw;
    }
}

function decodeHardware(key,raw) {
    switch (key) {
        case "prefix":
            return hex2ascii(raw);
        case "serialNum":
            return raw;
        case "firmwareVer":
            firmwareVer = raw.split("")
            return firmwareVer[0] + "." + firmwareVer[1];
        case "hardwareVer":
            hardwareVer = raw.split("")
            return hardwareVer[0] + "." + hardwareVer[1];
    }
}

