function parse(submsg, param, parLen, subindex) {
  var msg = submsg.substring(subindex, parLen);
  switch (param) {
    case "timestamp":
      var y = msg.substring(0, 4);
      var m = msg.substring(4, 6);
      var d = msg.substring(6, 8);
      var H = msg.substring(8, 10);
      var i = msg.substring(10, 12);
      var s = msg.substring(12, 14);
      return y + "-" + m + "-" + d + " " + H + ":" + i + ":" + s;
    case "latitude":
      var negatif = msg.substring(0, 1);
      var decNum = msg.substring(1, 4);
      var afterDec = msg.substring(4, 10);
      var neg = "";
      if (negatif == "0") {
        neg = "-";
      }
      return parseFloat(neg + decNum + "." + afterDec);
    case "longitude":
      negatif = msg.substring(0, 1);
      decNum = msg.substring(1, 4);
      afterDec = msg.substring(4, 10);
      neg = "";
      if (negatif == "0") {
        neg = "-";
      }
      return parseFloat(neg + decNum + "." + afterDec);
    case "velocity":
      negatif = msg.substring(0, 1);
      decNum = msg.substring(1, 3);
      afterDec = msg.substring(3, 6);
      neg = "";
      if (negatif == "0") {
        neg = "-";
      }
      return parseFloat(neg + decNum + "." + afterDec);
    case "heading":
      decNum = msg.substring(0, 3);
      afterDec = msg.substring(3, 4);
      return parseFloat(decNum + "." + afterDec);
    case "altitude":
      negatif = msg.substring(0, 1);
      decNum = msg.substring(1, 3);
      afterDec = msg.substring(3, 4);
      neg = "";
      if (negatif == "0") {
        neg = "-";
      }
      return parseFloat(neg + decNum + "." + afterDec);
    case "bat_lvl":
      return parseFloat(msg);
    case "mmsi":
      return msg.substring(1, 10);
    case "code":
      return parseInt(msg);
    case "power_plug_status":
      return String(msg);
    case "low_bat_status":
      return String(msg);
  }
}

function locParser(imei, device, cid, msg, msgMap) {
  var result = {
    postData: {},
    postUrl: "http://139.180.134.99/api/periodicposition",
    parser: "locParser",
  };

  var msgData = msgMap[cid]["data"];
  var table = msgMap[cid]["table"];
  var msgType = msgMap[cid]["type"];
  result.postData[table + "_device_imei"] = imei;
  result.postData[table + "_asset_id"] = device.asset_id;
  var lastIndex = 0;
  for (var i = 0; i < msgData.length; i++) {
    var parLen = msgMap["dataLen"][msgData[i]] + lastIndex;
    var parsed = parse(msg, msgData[i], parLen, lastIndex);
    if (msgData[i] == "timestamp") {
      result.postData[table + "_created_at"] = parsed;
      result.postData[table + "_updated_at"] = parsed;
    } else {
      result.postData[table + "_" + [msgData[i]]] = parsed;
    }
    // result.postData[table + "_type_id"] = msgType;
    lastIndex = parLen;
  }
  var splitType=msgType.split(",");
  if(splitType.length>1){
      result.postData[table + "_type_id"] = splitType[0];
      result.postData[table + "_status"] = splitType[1];
  }else{
      result.postData[table + "_type_id"] = msgType;
  }
  return result;
}

