function msgParser(
  msg,
  msgMap,
  imei,
  device,
  pingCmd,
  sbdLoc,
  ssasFlag,
  gpsFlag
) {
  // var msgLength = msg.substring(0, 4);
  var result;
  var parser;
  var cid = msg.substring(4, 6);
  if (cid == "85") {
    // flow.set("gps", false);
    gpsFlag = 0;
  }
  var restructed = restruct(msg, msgMap, cid, sbdLoc, ssasFlag, gpsFlag);
  msg = restructed.msg;
  if (pingCmd.includes(cid)) {
    result = locParser(imei, device, cid, msg.substring(6), msgMap);
  } else {
    switch (cid) {
      case "FA":
        if(msg.length>6){
            result = aisParser(imei, cid, msg.substring(6), msgMap);
            // result = {postData:{"imei": imei, "payload": msg}, postUrl: "http://139.180.134.99/api/parserais", parser: "php"};
        }else{
            var zeroData = {
                postData: {"imei": imei},
                postUrl:"http://139.180.134.99/api/aiskosong",
                parser: "aisEmpty"
            }
            result = zeroData;
        }
        break;
      case "80":
        result = ssasParser(
          imei,
          device,
          cid,
          msg.substring(6),
          msgMap,
          ssasFlag
        );
        break;
      // case "82":
      //     result = engineParser(imei, device, cid, msg.substring(6), msgMap);
      // case "83":
      //     result = flowParser(imei, device, cid, msg.substring(6), msgMap);
      case "84":
        result = ssasReset(imei, device, cid, msg.substring(6), msgMap);
        break;
      case "86":
        result = plugParser(imei, device, cid, msg.substring(6), msgMap);
        break;
      case "87":
        result = lowBattParser(imei, device, cid, msg.substring(6), msgMap);
        break;
    }
  }
  // result.restructed = restructed;
  result.cid = cid;
  result.topic =
    "UPDATE node_flags set gps=" +
    String(restructed.gpsFlag) +
    ", ssas=" +
    String(restructed.ssasFlag) +
    " where imei='" +
    String(imei) +
    "'";
  // result.sbdLoc=sbdLoc;
  return result;
}

