      } else if (latDeg > 10) {
        latDeg = "0" + String(latDeg);
      }
      if (lonDeg < 10) {
        lonDeg = "00" + String(lonDeg);
      } else if (lonDeg > 10 && lonDeg < 100) {
        lonDeg = "0" + String(lonDeg);
