function convertLat(latitude,north_south){
    var splitLat=latitude.split("."),
    secLat=splitLat[0].substring(splitLat[0].length - 2, splitLat[0].length)+'.'+splitLat[1],
    degLat=splitLat[0].substring(0,splitLat[0].length - 2),
    minLat=parseFloat(secLat)/60;
    if(north_south=='S'){
    	north_south=-1;
    }else{
    	north_south=1;
    }
    return String((parseFloat(degLat)+minLat)*north_south);
}

function convertLng(longitude,east_west){
    var splitLng=longitude.split("."),
    secLng=splitLng[0].substring(splitLng[0].length - 2, splitLng[0].length)+'.'+splitLng[1],
    degLng=splitLng[0].substring(0,splitLng[0].length - 2),
    minLng=parseFloat(secLng)/60;
    if(east_west=='W'){
    	east_west=-1;
    }else{
    	east_west=1;
    }
    return String((parseFloat(degLng)+minLng)*east_west);
}

function fixDateTime(date_stamp,time_stamp){
    var d = new Date(), date = date_stamp.substring(0,2), month = date_stamp.substring(2,4);
    var fixDateStamp=String(d.getUTCFullYear())+'-'+month+'-'+date;
    var fixTimeStamp=time_stamp.substring(0,2)+':'+time_stamp.substring(2,4)+':'+time_stamp.substring(4,6);
    return String(fixDateStamp+' '+fixTimeStamp);
}

